import React, { useState } from 'react'
import { Avatar, Button, Paper, Grid, Typography, Container } from '@material-ui/core';
import { GoogleOAuthProvider } from '@react-oauth/google';
import { GoogleLogin } from '@react-oauth/google';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import Icon from './icon';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import useStyles from './styles';
import Input from './Input';
import e from 'express';

const initialState = { firstName: '', lastName: '', email: '', password: '', confirmPassword: ''}



const Auth = () => {
    const classes = useStyles();
    const [showPassword, setShowPassword] = useState(false);
    const [isSignup, setIsSignup] = useState(false);
    const [formData, setFormData] = useState(initialState);
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const handleShowPassword = () => setShowPassword((prevShowPassword) => !prevShowPassword);

    const handleSubmit = (e) => {
      e.preventDefault();
      console.log(formData);
    };

    const handleChange = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value })
    };

    const switchMode = () => {
      setIsSignup((prevIsSignup) => !prevIsSignup);
      handleShowPassword(false);
    };

    const googleSuccess = async (res) => {
        const result = res?.profileObj; 
        const token = res?.tokenId;

        try {
          dispatch({ type: 'AUTH', data: { result, token }});
          navigate.push('/');
        } catch (error) {
          console.log(error);
        } 
    };
    const googleFailure = (error) => {
      console.log(error)
      console.log("Google Sign In was unsuccessful. Try Again Later");
    };

return (
  <GoogleOAuthProvider clientId="411748435462-1dtdekavoalv45b3ea0k55a9tc6mfhhc.apps.googleusercontent.com">
    <Container component="main" maxWidth="xs">
      <Paper className={classes.paper} elevation={3}>
            <Avatar className={classes.avatar}>
              <LockOutlinedIcon />
            </Avatar>
          <Typography variant="h5">
            {isSignup ? 'Sign Up' : 'Sign In'}
          <form className={classes.form} onSubmit={handleSubmit}>
            <Grid container spacing={2}>
              {
                isSignup && (
                  <>
                    <Input name="firstName" label="First Name" handleChange={handleChange} autoFocus half />
                    <Input name="lastName" label="Last Name" handleChange={handleChange} half />
                  </>
                )}
                <Input name="email" label="Email Address" handleChange={handleChange} type="email"/>
                <Input name="password" label="Password" handleChange={handleChange} type={showPassword ? "text" : "password"} handleShowPassword={handleShowPassword}/>
                { isSignup && <Input name="confirmPassword" label="Repeat Password" handleChange={handleChange} type="password" />}
            </Grid>
            <center>
            <Button type="submit" style={{ width: "200px",}} variant="contained" color="primary" className={classes.submit}>
              {isSignup ? 'Sign Up' : 'Sign In'}
            </Button>
            <GoogleLogin
              render={(renderProps) => (
                <Button className={classes.googleButton} color='primary' fullWidth variant="contained" onClick={renderProps.onClick} disabled={renderProps.disabled} startIcon={<Icon />}>
                  Google Sign In
                </Button>
              )}
              onSuccess={googleSuccess}
              onFailure={googleFailure}
              cookiePolicy="single_host_origin"
            />
            </center>
            <Grid container justifyContent="flex-end">
              <Grid item>
                <br/>
                <Button onClick={switchMode}>
                  { isSignup ? 'Already have an account? Sign In' : "Dont have an account? Sign Up"}
                </Button>
              </Grid>

            </Grid>
          </form>
        </Typography>      
        </Paper>
      </Container>
  </GoogleOAuthProvider>
  )
}

export default Auth;
