import axios from 'axios';

const API = axios.create({ baseURL: 'https://charms.herokuapp.com/' });

export const fetchPosts = () => API.get(API);
export const createPost = (newPost) => API.post(API, newPost);
export const updatePost = (id, updatedPost) => API.patch(`${API}/${id}`, updatedPost);
export const deletePost = (id) => API.delete(`${API}/${id}`);
export const likePost = (id) => API.patch(`${API}/${id}/likePost`);
